import React from "react";
import { Link } from "react-router-dom";
import ListLink from "./ListLink";
import { Layout, Menu, Dropdown, Avatar } from "antd";
import { FacebookFilled, DownOutlined, UserOutlined } from "@ant-design/icons";
import data from "../Data/Profile.json";

class Header extends React.Component {
  constructor() {
    super();
  }

  render() {
    const { Header } = Layout;
    const menu = (
      <Menu>
        <Menu.Item>
          <Link to="/friendlist">ดูรายชื่อเพื่อน</Link>
        </Menu.Item>
        <Menu.Item>
          <Link to="/changepassword">เปลี่ยนรหัสผ่าน</Link>
        </Menu.Item>
        <Menu.Divider />
        <Menu.Item>
          <Link to="/login">ออกจากระบบ</Link>
        </Menu.Item>
      </Menu>
    );
    return (
      <Header style={{ position: "fixed", zIndex: 1, width: "100%" }}>
        <div className="logo">
          <Link to="/">
            <FacebookFilled />
          </Link>
        </div>
        <ListLink />
        <div className="right nav-profile">
          <span>
            <Link to="/profile">
              {data.profilename}
              <Avatar icon={<UserOutlined />} className="ml-10 mr-10" />
            </Link>
          </span>
          <Dropdown overlay={menu}>
            <a
              className="ant-dropdown-link"
              onClick={(e) => e.preventDefault()}
            >
              <DownOutlined />
            </a>
          </Dropdown>
        </div>
      </Header>
    );
  }
}

export default Header;
